        return -1;
    }


    // Deixa a lista vazia, com zero elementos
    void clear() {
        _clear(root);
        root = NULL;
    }

    void _clear(Node* current) {
        if(current == NULL) return;
        _clear(current->left);
        _clear(current->right);
        delete current;
    }

    // Devolve true se vazia e false caso contrario
    bool empty() {
        return root == NULL;
    }

    // Printa as informacoes da lista
    void print() {
        std::cout << "BinaryTree: ";
