    cout << "insert 45" << endl; tree.insert_left(node32, 45);
    cout << "insert 12" << endl; tree.insert_right(node32, 12);
    cout << "insert 10" << endl; tree.insert_left(node32->left, 10);
    cout << "insert 37" << endl; tree.insert_right(node32->left, 37);
