package main

import (
	"fmt"
	"strings"
)

type Node[T any] struct {
	Value T
	next  *Node[T]
	prev  *Node[T]
	root  *Node[T]
}

// Retorna o próximo nó
func (n *Node[T]) Next() *Node[T] {
	return n.next
}

// Retorna o nó anterior
func (n *Node[T]) Prev() *Node[T] {
	return n.prev
}

type List[T any] struct {
	root *Node[T]
	size int
}

func NewList[T any]() *List[T] {
	root := &Node[T]{}
	root.next = root
	root.prev = root
	root.root = root
	return &List[T]{root: root, size: 0}
}

func (l *List[T]) PushBack(value T) {
	l.Insert(l.root, value)
}

func (l *List[T]) Insert(mark *Node[T], value T) *Node[T] {
	n := &Node[T]{Value: value, root: l.root}
	n.prev = mark.prev
	n.next = mark
	mark.prev.next = n
	mark.prev = n
	l.size++
	return n
}

func (l *List[T]) Size() int {
	return l.size
}

func (l *List[T]) Clear() {
	l.root.next = l.root
	l.root.prev = l.root
	l.size = 0
}

func (l *List[T]) End() *Node[T] {
	return l.root
}

func (l *List[T]) Front() *Node[T] {
	if l.size == 0 {
		return l.root
	}
	return l.root.next
}

func (l *List[T]) Back() *Node[T] {
	if l.size == 0 {
		return l.root
	}
	return l.root.prev
}

func (l *List[T]) Remove(n *Node[T]) *Node[T] {
	if n == l.root {
		return l.root
	}
	n.prev.next = n.next
	n.next.prev = n.prev
	l.size--
	return n.next
}

func (l *List[T]) IndexOf(n *Node[T]) int {
	if n == l.root {
		return l.size
	}
	if n.root != l.root {
		return -1
	}
	i := 0
	for it := l.root.next; it != n; it = it.next {
		i++
		if i >= l.size {
			return -1
		}
	}
	return i
}

func (l *List[T]) String() string {
	values := []string{}
	for n := l.root.next; n != l.root; n = n.next {
		values = append(values, fmt.Sprint(n.Value))
	}
	return "[" + strings.Join(values, ", ") + "]"
}

package main

import (
	"fmt"

	"github.com/gdamore/tcell/v2"
)

type Editor struct {
	lines  *List[*List[rune]]
	line   *Node[*List[rune]]
	cursor *Node[rune]
	screen tcell.Screen
	style  tcell.Style
}

func (e *Editor) InsertChar(r rune) {
	e.cursor = e.line.Value.Insert(e.cursor, r)
	e.cursor = e.cursor.Next()
}

func (e *Editor) KeyLeft() {
	if e.cursor != e.line.Value.Front() { // Se o cursor não está no início da linha
		e.cursor = e.cursor.Prev() // Move o cursor para a esquerda
		return
	}
	// Estamos no início da linha
	if e.line != e.lines.Front() { // Se não está na primeira linha
		e.line = e.line.Prev()        // Move para a linha anterior
		e.cursor = e.line.Value.End() // Move o cursor para o final da linha
	}
}

func (e *Editor) KeyEnter() {
}

func (e *Editor) KeyRight() {
}

func (e *Editor) KeyUp() {
}

func (e *Editor) KeyDown() {
}

func (e *Editor) KeyBackspace() {
}

func (e *Editor) KeyDelete() {
}

func main() {
	// Texto inicial e posição do cursor
	editor := NewEditor()
	editor.Draw()
	editor.MainLoop()
	defer editor.screen.Fini() // Encerra a tela ao sair
}

func (e *Editor) MainLoop() {
	for {
		ev := e.screen.PollEvent()
		switch ev := ev.(type) {
		case *tcell.EventKey:
			switch ev.Key() {
			case tcell.KeyEsc, tcell.KeyCtrlC:
				return
			case tcell.KeyEnter:
				e.KeyEnter()
			case tcell.KeyLeft:
				e.KeyLeft()
			case tcell.KeyRight:
				e.KeyRight()
			case tcell.KeyUp:
				e.KeyUp()
			case tcell.KeyDown:
				e.KeyDown()
			case tcell.KeyBackspace, tcell.KeyBackspace2:
				e.KeyBackspace()
			case tcell.KeyDelete:
				e.KeyDelete()
			default:
				if ev.Rune() != 0 {
					e.InsertChar(ev.Rune())
				}
			}
			e.Draw()
		case *tcell.EventResize:
			e.screen.Sync()
			e.Draw()
		}
	}
}

func NewEditor() *Editor {
	e := &Editor{}
	// Inicializa a tela
	screen, err := tcell.NewScreen()
	if err != nil {
		fmt.Printf("erro ao criar a tela: %v", err)
	}
	if err := screen.Init(); err != nil {
		fmt.Printf("erro ao iniciar a tela: %v", err)
	}
	e.screen = screen
	e.lines = NewList[*List[rune]]()
	e.lines.PushBack(NewList[rune]())
	e.line = e.lines.Front()
	e.cursor = e.line.Value.Back()
	// Define o estilo do texto (branco com fundo preto)
	e.style = tcell.StyleDefault.Foreground(tcell.ColorWhite).Background(tcell.ColorBlack)

	// Limpa a tela e define o estilo base
	e.screen.SetStyle(e.style)
	e.screen.Clear()
	return e
}

func (e *Editor) Draw() {
	e.screen.Clear()
	x := 0
	y := 0
	for line := e.lines.Front(); line != e.lines.End(); line = line.Next() {
		for char := line.Value.Front(); ; char = char.Next() {
			data := char.Value
			if char == line.Value.End() {
				data = '⤶'
			}
			if data == ' ' {
				data = '·'
			}
			if char == e.cursor {
				e.screen.SetContent(x, y, data, nil, e.style.Reverse(true))
			} else {
				e.screen.SetContent(x, y, data, nil, e.style)
			}
			x++
			if char == line.Value.End() {
				break
			}
		}
		y++
		x = 0
	}
	e.screen.Show()
}
