Node* clone(stringstream& ss) {
    string value;
    ss >> value;
    if(value == "#")
        return nullptr;
    int num;
    stringstream(value) >> num;
    Node* node = new Node(num);
    node->left = clone(ss);
    node->right = clone(ss);
    return node;
}


int main(){
    string line;
    getline(cin, line);
    stringstream ss(line);
    Node* root = clone(ss);
    bshow(root);
    my_show(root);
}

